{let e=()=>{let e=$("#new-comment-form");e.submit((n=>{n.preventDefault(),$.ajax({type:"POST",url:"/comments/create",data:e.serialize(),success:e=>{console.log(e);let n=t(e.data.comment);$("#post-comments-list >ul").prepend(n),o($(" .delete-comment-button",n)),new Noty({theme:"relax",text:"Comment made!",type:"success",layout:"topRight",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))},t=e=>$(`<li id="comment-${e._id}">\n    <p>\n        <small>\n            <a class="delete-comment-button" href="/comments/destroy/${e._id}">DELETE</a>\n        </small>\n                ${e.content}\n        <br>\n        <small>\n                ${e.user.name}\n        </small>\n        <small> \n            <a href="/likes/toggle/?id=${e._id}&type=Comment" class="toggle-like-button" data-likes="0">\n                0 Likes\n            </a> \n        </small>\n    </p>\n        </li>`),o=e=>{$(e).click((t=>{t.preventDefault(),$.ajax({type:"GET",url:$(e).prop("href"),success:e=>{$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:e=>{console.log(e.responseText)}})}))};e()}